eval(%w(n=1;cake_color_sets={"🍊\\sMikan":[220,221,222,223],"🍵\\sMatcha":[70,71,106,107],"🌸\\sSakura":[210,216,217,218],"🍫\\sChoco":[130,172,173,174]};adzuki_colors=[95,96,131,132];kuri_color=178;symbols=["▄▄","█\\s","▀▀","\\s█","▟█","█▙","█▛","▜█","██"];rows=5;cols=5;grid=Array.new(rows){Array.new(cols)};fill_order=Array.new(rows){Array.new(cols)};x,y=0,0;dx,dy=0,1;cake_color_keys=cake_color_sets.keys;color_index=0;loop{cake_colors=cake_color_sets[cake_color_keys[color_index]];current_color_set=cake_color_keys[color_index];color_index=(color_index+1)%cake_color_keys.size;grid=Array.new(rows){Array.new(cols)};fill_order=Array.new(rows){Array.new(cols)};x,y=0,0;dx,dy=0,1;n=1;loop{nx,ny=x+dx,y+dy;if(n==rows*cols)then(grid[x][y]=symbols[8];fill_order[x][y]=n);elsif(nx<0||nx>=rows||ny<0||ny>=cols||!grid[nx][ny].nil?)then(if(dx==0&&dy==1)then(grid[x][y]=symbols[5]);elsif(dx==1&&dy==0)then(grid[x][y]=symbols[6]);elsif(dx==0&&dy==-1)then(grid[x][y]=symbols[7]);elsif(dx==-1&&dy==0)then(grid[x][y]=symbols[4])end;fill_order[x][y]=n;dx,dy=dy,-dx;nx,ny=x+dx,y+dy)else(if(dx==0&&dy==1)then(grid[x][y]=symbols[0]);elsif(dx==1&&dy==0)then(grid[x][y]=symbols[1]);elsif(dx==0&&dy==-1)then(grid[x][y]=symbols[2]);elsif(dx==-1&&dy==0)then(grid[x][y]=symbols[3])end;fill_order[x][y]=n)end;x,y=nx,ny;print"\\e[2J";print"\\e[1m\\e[38;5;#{cake_colors[0]}m#{current_color_set}\\e[0m";print"\\n\\n";grid.each_with_index{|row,i|row.each_with_index{|cell,j|order=fill_order[i][j]||0;if(order>0)then(bg_color=cake_colors[(order-1)%cake_colors.size];text_color=adzuki_colors[(order-1)%adzuki_colors.size];if(cell==symbols[8])then(print"\\e[48;5;#{bg_color}m\\e[38;5;#{kuri_color}m\\e[1m#{cell||"\\s\\s"}\\e[0m");elsif(symbols[4..7].include?(cell))then(print"\\e[38;5;#{bg_color}m\\e[1m#{cell}\\e[0m")else(print"\\e[48;5;#{bg_color}m\\e[38;5;#{text_color}m\\e[1m#{cell||"\\s\\s"}\\e[0m")end)else(print"\\s\\s")end};print"\\n"};if(n>rows*cols)then(break;)end;n+=1;sleep(0.1)}})*"")